import React, { useState, useEffect } from 'react';
import { ref, set, get, child } from 'firebase/database';
import { database } from './firebase'; // Import the database
import { PlusOutlined } from '@ant-design/icons';
import { Button, Col, Drawer, Form, Input, Row, Space, Table, Tag, Modal, Select } from 'antd';

const { Option } = Select;

const App = () => {
  const [data, setData] = useState([]);
  const [isModalVisible, setIsModalVisible] = useState(false);
  const [currentRecord, setCurrentRecord] = useState(null);
  const [form] = Form.useForm();
  const [open, setOpen] = useState(false);
  const [drawerForm] = Form.useForm();

  useEffect(() => {
    // Retrieve data from Firebase on component mount
    const fetchData = async () => {
      const dbRef = ref(database);
      const snapshot = await get(child(dbRef, 'accounts'));
      const accounts = snapshot.exists() ? Object.values(snapshot.val()) : [];
      setData(accounts);
    };
    fetchData();
  }, []);

  const showDrawer = () => {
    setOpen(true);
  };

  const onClose = () => {
    setOpen(false);
    drawerForm.resetFields();
  };

  const showEditModal = (record) => {
    setCurrentRecord(record);
    form.setFieldsValue(record);
    setIsModalVisible(true);
  };

  const handleDelete = (key) => {
    const updatedData = data.filter((item) => item.key !== key);
    setData(updatedData);
    set(ref(database, `accounts/${key}`), null);
  };

  const handleOk = () => {
    form
      .validateFields()
      .then((values) => {
        const updatedData = data.map((item) =>
          item.key === currentRecord.key ? { ...item, ...values } : item
        );
        setData(updatedData);
        set(ref(database, `accounts/${currentRecord.key}`), values);
        setIsModalVisible(false);
        setCurrentRecord(null);
        form.resetFields();
      })
      .catch((info) => {
        console.log('Validate Failed:', info);
      });
  };

  const handleCancel = () => {
    setIsModalVisible(false);
    setCurrentRecord(null);
    form.resetFields();
  };

  const handleDrawerSubmit = () => {
    drawerForm
      .validateFields()
      .then((values) => {
        const newData = {
          ...values,
          key: Date.now().toString(), // Use a timestamp as a unique key
          tags: values.tags.split(',').map((tag) => tag.trim()),
        };
        setData([...data, newData]);
        set(ref(database, `accounts/${newData.key}`), newData);
        setOpen(false);
        drawerForm.resetFields();
      })
      .catch((info) => {
        console.log('Validate Failed:', info);
      });
  };

  const columns = [
    {
      title: 'Name',
      dataIndex: 'name',
      key: 'name',
      render: (text) => <a>{text}</a>,
    },
    {
      title: 'Age',
      dataIndex: 'age',
      key: 'age',
    },
    {
      title: 'Skills',
      dataIndex: 'skills',
      key: 'skills',
    },
    {
      title: 'Tags',
      key: 'tags',
      dataIndex: 'tags',
      render: (_, { tags }) => (
        <>
          {tags.map((tag) => {
            let color = tag.length > 5 ? 'geekblue' : 'green';
            if (tag === 'Busy') {
              color = 'volcano';
            }
            return (
              <Tag color={color} key={tag}>
                {tag.toUpperCase()}
              </Tag>
            );
          })}
        </>
      ),
    },
    {
      title: 'Action',
      key: 'action',
      render: (_, record) => (
        <Space size="middle">
          <a onClick={() => showEditModal(record)}>Edit</a>
          <a onClick={() => handleDelete(record.key)}>Delete</a>
        </Space>
      ),
    },
  ];

  return (
    <>
      <Button type="primary" onClick={showDrawer} icon={<PlusOutlined />}>
        New account
      </Button>
      <Table columns={columns} dataSource={data} />
      <Drawer
        title="Create a new account"
        width={720}
        onClose={onClose}
        visible={open}
        bodyStyle={{
          paddingBottom: 80,
        }}
        extra={
          <Space>
            <Button onClick={onClose}>Cancel</Button>
            <Button onClick={handleDrawerSubmit} type="primary">
              Submit
            </Button>
          </Space>
        }
      >
        <Form layout="vertical" hideRequiredMark form={drawerForm}>
          <Row gutter={16}>
            <Col span={12}>
              <Form.Item
                name="name"
                label="Name"
                rules={[
                  {
                    required: true,
                    message: 'Please enter user name',
                  },
                ]}
              >
                <Input placeholder="Please enter user name" />
              </Form.Item>
            </Col>
            <Col span={12}>
              <Form.Item
                name="age"
                label="Age"
                rules={[
                  {
                    required: true,
                    message: 'Please enter age',
                  },
                ]}
              >
                <Input type="number" placeholder="Please enter age" />
              </Form.Item>
            </Col>
          </Row>
          <Row gutter={16}>
            <Col span={12}>
              <Form.Item
                name="skills"
                label="Skills"
                rules={[
                  {
                    required: true,
                    message: 'Please enter skills',
                  },
                ]}
              >
                <Input placeholder="Please enter skills" />
              </Form.Item>
            </Col>
            <Col span={12}>
              <Form.Item
                name="tags"
                label="Tags"
                rules={[
                  {
                    required: true,
                    message: 'Please enter tags (comma separated)',
                  },
                ]}
              >
                <Input placeholder="Please enter tags (comma separated)" />
              </Form.Item>
            </Col>
          </Row>
        </Form>
      </Drawer>
      <Modal
        title="Edit Record"
        visible={isModalVisible}
        onOk={handleOk}
        onCancel={handleCancel}
      >
        <Form form={form} layout="vertical" name="form_in_modal">
          <Form.Item
            name="name"
            label="Name"
            rules={[{ required: true, message: 'Please input the name!' }]}
          >
            <Input />
          </Form.Item>
          <Form.Item
            name="age"
            label="Age"
            rules={[{ required: true, message: 'Please input the age!' }]}
          >
            <Input type="number" />
          </Form.Item>
          <Form.Item
            name="skills"
            label="Skills"
            rules={[{ required: true, message: 'Please input the skills!' }]}
          >
            <Input />
          </Form.Item>
          <Form.Item
            name="tags"
            label="Tags"
            rules={[{ required: true, message: 'Please input the tags!' }]}
          >
            <Input />
          </Form.Item>
        </Form>
      </Modal>
    </>
  );
};

export default App;
