// src/App.js
import React, { useEffect, useState } from 'react';
import { API, graphqlOperation } from 'aws-amplify';

import { listAccounts } from './graphql/queries';
import { createAccount } from './graphql/mutations';
import Dashboard from './components/Dashboard';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import './App.css';
import 'antd/dist/antd.css'; // Import Ant Design styles

function App() {
  const [accounts, setAccounts] = useState([]);
  const [name, setName] = useState('');

  useEffect(() => {
    const fetchAccounts = async () => {
      try {
        const accountData = await API.graphql(graphqlOperation(listAccounts));
        setAccounts(accountData.data.listAccounts.items);
      } catch (error) {
        console.log('Error fetching accounts', error);
      }
    };

    fetchAccounts();
  }, []);

  const addAccount = async () => {
    try {
      const newAccount = { name };
      await API.graphql(graphqlOperation(createAccount, { input: newAccount }));
      setAccounts([...accounts, newAccount]);
      setName('');
    } catch (error) {
      console.log('Error creating account', error);
    }
  };

  return (
    <Router>
      <Routes>
        <Route path="/" element={<Dashboard accounts={accounts} addAccount={addAccount} setName={setName} name={name} />} />
      </Routes>
    </Router>
  );
}

export default App;
