import React, { useState } from 'react';
import { Space, Table, Tag, Button, Modal, Form, Input } from 'antd';
import { database } from '../firebase'; // Adjust path if necessary

const Members = ({ data, onEdit, onDelete }) => {
  const [isModalVisible, setIsModalVisible] = useState(false);
  const [form] = Form.useForm();

  const showModal = () => {
    setIsModalVisible(true);
  };

  const handleCancel = () => {
    setIsModalVisible(false);
    form.resetFields();
  };

  const handleOk = () => {
    form.validateFields()
      .then((values) => {
        const newData = {
          ...values,
          key: Date.now().toString(), // Unique key using timestamp
          tags: values.tags.split(',').map(tag => tag.trim()),
        };
        setIsModalVisible(false);
        form.resetFields();

        // Update local state (data array) and Firebase
        const updatedData = [...data, newData];
        database.ref(`accounts/${newData.key}`).set(newData); // Save to Firebase
        // setData(updatedData); // Update local state if needed
      })
      .catch((errorInfo) => {
        console.log('Validation Failed:', errorInfo);
      });
  };

  const columns = [
    {
      title: 'Name',
      dataIndex: 'name',
      key: 'name',
      render: (text) => <a>{text}</a>,
    },
    {
      title: 'Age',
      dataIndex: 'age',
      key: 'age',
    },
    {
      title: 'Skills',
      dataIndex: 'skills',
      key: 'skills',
    },
    {
      title: 'Tags',
      key: 'tags',
      dataIndex: 'tags',
      render: (_, { tags }) => (
        <>
          {tags.map((tag) => {
            let color = tag.length > 5 ? 'geekblue' : 'green';
            if (tag === 'Busy') {
              color = 'volcano';
            }
            return (
              <Tag color={color} key={tag}>
                {tag.toUpperCase()}
              </Tag>
            );
          })}
        </>
      ),
    },
    {
      title: 'Action',
      key: 'action',
      render: (_, record) => (
        <Space size="middle">
          <a onClick={() => onEdit(record)}>Edit</a>
          <a onClick={() => onDelete(record.key)}>Delete</a>
        </Space>
      ),
    },
  ];

  return (
    <>
      <h1>Members</h1>
      <Button type="primary" onClick={showModal} style={{ marginBottom: '16px' }}>
        Add Member
      </Button>
      <Modal
        title="Add Member"
        visible={isModalVisible}
        onOk={handleOk}
        onCancel={handleCancel}
      >
        <Form form={form} layout="vertical">
          <Form.Item
            name="name"
            label="Name"
            rules={[{ required: true, message: 'Please enter member name' }]}
          >
            <Input />
          </Form.Item>
          <Form.Item
            name="age"
            label="Age"
            rules={[{ required: true, message: 'Please enter member age' }]}
          >
            <Input />
          </Form.Item>
          <Form.Item
            name="skills"
            label="Skills"
            rules={[{ required: true, message: 'Please enter member skills' }]}
          >
            <Input />
          </Form.Item>
          <Form.Item
            name="tags"
            label="Tags"
            rules={[{ required: true, message: 'Please enter member tags' }]}
          >
            <Input />
          </Form.Item>
        </Form>
      </Modal>
      <Table columns={columns} dataSource={data} />
    </>
  );
};

export default Members;
